// from seconds
export function formatTime(num) {
  if (num < 3600) {
    var minutes = padZero(Math.floor(num / 60), 2); // const seconds = padZero(num % 60, 2);
    // return `${minutes}:${seconds}`;

    return "".concat(minutes, " Min");
  } else {
    var hours = padZero(Math.floor(num / 3600), 2);

    var _minutes = padZero(Math.floor((num - hours * 3600) / 60), 2); // const seconds = padZero(num % 60, 2);
    // return `${hours}:${minutes}:${seconds}`;


    return "".concat(hours, " H ").concat(_minutes, " Min");
  }
}

function padZero(num, size) {
  var s = String(num);

  while (s.length < size) {
    s = "0".concat(s);
  }

  return s;
}
